import { useContext } from "react"
import { StoreContext } from "../Providers/Store"

export default function Card() {
    const { movies, setMovie } = useContext(StoreContext)

    return (
        <div>
            <Header/>
            <h1>List Movies</h1>
            {
                movies.map((movie, index) => {
                    return (
                        <div>
                            <p>{movie.title}</p>
                        </div>
                    )
                })
            }
        </div>
    )
}
