import styled from 'styled-components'

export const AddMovieContainer = styled.div`
    height: calc(100vh - 70px);
    width: 100vw;
    background-color: #A8C5C5;
    display: flex;
    flex-direction: column;
    text-align: center;
    align-items: center;

    h1 {
        margin-top: 50px;
    }

    input {
        height: 50px;
        width: 300px;
        margin-top: 35px;
    }

    button {
        width: 300px;
        height: 40px;
    }

    .pages {
        text-align: center;
    }

`
