import styled from 'styled-components'

export const CardsContainer = styled.div`
    height: calc(100vh - 70px);
    width: 100vw;
    background-color: #A8C5C5;
    display: flex;
    flex-direction: column;

    h1 {
        text-align: center;
    }

    .ensemble-cards {
        display: flex;
        border: 1px solid blue;
    }

    .card {
        background-color: white;
        width: max-content;
    }
`
